import React from "react";
import PropTypes from "prop-types";
import { Badge, Col, Drawer, Image, Row } from "antd";
import { PlusOutlined, StopOutlined } from "@ant-design/icons";
ImageDisplay.propTypes = {
  imageArray: PropTypes.array,
  onOpenImg: PropTypes.func,
  onDelete: PropTypes.func,
};

ImageDisplay.defaultProps = {
  imageArray: [],
  onOpenImg: null,
  onDelete: null,
};

function ImageDisplay(props) {
  const { imageArray, onOpenImg, onDelete } = props;
  const onPickImage = () => {
    if (onOpenImg) onOpenImg();
  };
  const handleRemove = (id) => {
    console.log(id)
    if (onDelete) onDelete(id);
  };
  return (
    <div>
      {imageArray && imageArray.length ? (
       <Row justify="end" align="middle" gutter={10}>
       {imageArray.map((image, index) => {
         if (index == 0) {
           return (
             <Col
               span={24}
               style={{ display: "flex", justifyContent: "center" }}
               key={index}
             >
               <Badge
                 count={
                   <StopOutlined
                     onClick={() => handleRemove(image._id)}
                     style={{ color: "red" }}
                   />
                 }
                 
               >
                 <img
                   src={`${image?.source}`}
                   alt={image._id}
                   style={{ width: "400px" }}
                 />
               </Badge>
             </Col>
           );
         }
         return (
           <Col span={6} key={index}>
             <Badge
               count={
                 <StopOutlined
                   onClick={() => handleRemove(image._id)}
                   style={{ color: "red" }}
                 />
               }
               style={{ width: "100%" }}
             >
               <img
                 src={`${image?.source}`}
                 alt="Small"
                 style={{ width: "100%" }}
               />
             </Badge>
           </Col>
         );
       })}
       <Col>
         <div
           style={{
             width: "60px",
             height: "60px",
             display: "flex",
             justifyContent: "center",
             alignItems: "center",
             border: "1px solid grey",
             margin: "10px 0px",
             cursor: "pointer",
           }}
           onClick={onPickImage}
         >
           <PlusOutlined />
         </div>
       </Col>
     </Row>
      ) : (
        <div
        style={{ display: "flex", justifyContent: "center" }}
        onClick={onPickImage}
      >
        <Image
          width={380}
          height={380}
          style={{objectFit:'cover'}}
          src="error"
          fallback="data:image/png;base64,"
        />
      </div>
      )}
    </div>
  );
}

export default ImageDisplay;
